import React, { Component, useEffect, useState } from "react";
import "./App.css";
import { Button } from "./components/Button";
import { Input } from "./components/Input";
import { Result } from "./components/Result";
import { ClearButton } from "./components/ClearButton";
import * as math from "mathjs";
import io from 'socket.io-client'

const socket = io.connect('http://localhost:4000')



class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      input: "",
      results: [],
    };


  }

  componentDidMount() {
    socket.on('calculation', ({ results }) => {
    this.setState((prevState) => {
      let calculations = prevState.results
      calculations.push(results)
      this.state.results = calculations;
    })
  })
}


  addToInput = val => {
    this.setState({ input: this.state.input + val });
  };

  handleEqual = () => {
    this.setState({ input: math.evaluate(this.state.input),
                    results: [...this.state.results, this.state.input + " = " + math.evaluate(this.state.input)]
                  });

   setTimeout(() => {
    const { results } = this.state;
    socket.emit('calculation', { results })
}, 10);


  };


  render() {
    return (
      <div className="app">
        <div className="calc-wrapper">
          <Input input={this.state.input} />
          <div className="row">
            <Button handleClick={this.addToInput}>7</Button>
            <Button handleClick={this.addToInput}>8</Button>
            <Button handleClick={this.addToInput}>9</Button>
            <Button handleClick={this.addToInput}>/</Button>
          </div>
          <div className="row">
            <Button handleClick={this.addToInput}>4</Button>
            <Button handleClick={this.addToInput}>5</Button>
            <Button handleClick={this.addToInput}>6</Button>
            <Button handleClick={this.addToInput}>*</Button>
          </div>
          <div className="row">
            <Button handleClick={this.addToInput}>1</Button>
            <Button handleClick={this.addToInput}>2</Button>
            <Button handleClick={this.addToInput}>3</Button>
            <Button handleClick={this.addToInput}>+</Button>
          </div>
          <div className="row">
            <Button handleClick={this.addToInput}>.</Button>
            <Button handleClick={this.addToInput}>0</Button>
            <Button handleClick={() => this.handleEqual()}>=</Button>
            <Button handleClick={this.addToInput}>-</Button>
          </div>
          <div className="row">
            <ClearButton handleClear={() => this.setState({ input: "" })}>
              Clear
            </ClearButton>
          </div>
            <Result input={this.state.results} />
        </div>
      </div>
    );
  }
}

export default App;
